import React, { useState } from "react";
import {
  View,
  Text,
  TextInput,
  Button,
  SafeAreaView,
  ActivityIndicator,
  StyleSheet,
  FlatList,
  ScrollView,
} from "react-native";
import ApiClient from "../api/ApiClient";

const styles = StyleSheet.create({
  container: {
    flex: 1,
    paddingTop: 20,
    paddingHorizontal: 10,
    backgroundColor: "#fff",
  },
  itemContainer: {
    marginBottom: 20,
    borderWidth: 1,
    borderColor: "black",
    padding: 10,
  },
});

const AnimeListScreen = () => {
  const [animeList, setAnimeList] = useState([]);
  const [search, setSearch] = useState("");
  const [loading, setLoading] = useState(false);

  const fetchAnimeList = async (search) => {
    setLoading(true);
    try {
      const response = await ApiClient.get(
        `https://api.jikan.moe/v4/anime?q=${search}&limit=3`
      );
      console.log("Response data:", response.data);
      setAnimeList(response.data.data); // Use 'data' key instead of 'results'
    } catch (error) {
      if (error.response && error.response.status === 429) {
        console.error(
          "Error fetching anime list: Too many requests, please wait and try again later."
        );
        alert("Too many requests, please wait and try again later.");
      } else {
        console.error("Error fetching anime list:", error);
      }
    } finally {
      setLoading(false);
    }
  };

  const handleSearch = () => {
    fetchAnimeList(search);
  };

  const renderItem = ({ item }) => {
    console.log(item);
    return (
      <View style={styles.itemContainer}>
        <Text>ID: {item.mal_id}</Text>
        <Text>Title: {item.title}</Text>
        <Text>Synopsis: {item.synopsis}</Text>
      </View>
    );
  };

  return (
    <SafeAreaView style={styles.container}>
      <TextInput
        style={{ height: 40, borderColor: "gray", borderWidth: 1 }}
        onChangeText={(text) => setSearch(text)}
        value={search}
        placeholder="Search for an anime"
      />
      <Button title="Search" onPress={handleSearch} />
      {loading ? (
        <View style={{ flex: 1, justifyContent: "center" }}>
          <ActivityIndicator size="large" color="#0000ff" />
        </View>
      ) : animeList && animeList.length > 0 ? (
        <ScrollView style={{ flex: 1 }}>
          <FlatList
            data={animeList}
            renderItem={renderItem}
            keyExtractor={(item) => item.mal_id.toString()}
          />
        </ScrollView>
      ) : (
        <Text>No results found.</Text>
      )}
    </SafeAreaView>
  );
};

export default AnimeListScreen;
