import React, { useState } from "react";
import {
  View,
  Text,
  TextInput,
  Button,
  SafeAreaView,
  ActivityIndicator,
  StyleSheet,
  FlatList,
  ScrollView,
} from "react-native";
import ApiClient from "../api/ApiClient";

const styles = StyleSheet.create({
  container: {
    flex: 1,
    paddingTop: 20,
    paddingHorizontal: 10,
    backgroundColor: "#fff",
  },
  itemContainer: {
    marginBottom: 20,
    borderWidth: 1,
    borderColor: "black",
    padding: 10,
    marginHorizontal: 10, // Add margin to the sides of the containers
  },
  label: {
    fontSize: 16, // Larger font size for labels
    fontWeight: "bold",
  },
  value: {
    fontSize: 14, // Regular font size for values
  },
});

const AnimeListScreen = () => {
  const [animeList, setAnimeList] = useState([]);
  const [search, setSearch] = useState("");
  const [loading, setLoading] = useState(false);

  const fetchAnimeList = async (search) => {
    setLoading(true);
    try {
      const response = await ApiClient.get(
        `https://api.jikan.moe/v4/anime?q=${search}&limit=3`
      );
      console.log("Response data:", response.data);
      setAnimeList(response.data.data); // Use 'data' key instead of 'results'
    } catch (error) {
      if (error.response && error.response.status === 429) {
        console.error(
          "Error fetching anime list: Too many requests, please wait and try again later."
        );
        alert("Too many requests, please wait and try again later.");
      } else {
        console.error("Error fetching anime list:", error);
      }
    } finally {
      setLoading(false);
    }
  };

  const handleSearch = () => {
    fetchAnimeList(search);
  };

  const renderItem = ({ item }) => {
    const genres = item.genres.map((genre) => genre.name).join(", ");
  
    return (
      <View style={styles.itemContainer}>
        <Text style={styles.label}>ID:</Text>
        <Text style={styles.value}>{item.mal_id}</Text>
        <Text style={styles.label}>Title:</Text>
        <Text style={styles.value}>{item.title}</Text>
        <Text style={styles.label}>Synopsis:</Text>
        <Text style={styles.value}>{item.synopsis}</Text>
        <Text style={styles.label}>Genres:</Text>
        <Text style={styles.value}>{genres}</Text>
        <Text style={styles.label}>Start Date:</Text>
        <Text style={styles.value}>{item.aired.from}</Text>
        <Text style={styles.label}>End Date:</Text>
        <Text style={styles.value}>{item.aired.to || "Ongoing"}</Text>
        <Text style={styles.label}>Rating:</Text>
        <Text style={styles.value}>{item.rating}</Text>
        <Text style={styles.label}>Type:</Text>
        <Text style={styles.value}>{item.type}</Text>
      </View>
    );
  };

  return (
    <SafeAreaView style={styles.container}>
      <TextInput
        style={{ height: 40, borderColor: "gray", borderWidth: 1 }}
        onChangeText={(text) => setSearch(text)}
        value={search}
        placeholder="Search for an anime"
      />
      <Button title="Search" onPress={handleSearch} />
      {loading ? (
        <View style={{ flex: 1, justifyContent: "center" }}>
          <ActivityIndicator size="large" color="#0000ff" />
        </View>
      ) : animeList && animeList.length > 0 ? (
        <FlatList
          data={animeList}
          renderItem={renderItem}
          keyExtractor={(item) => item.mal_id.toString()}
          contentContainerStyle={{ paddingBottom: 20 }}
        />
      ) : (
        <Text>No results found.</Text>
      )}
    </SafeAreaView>
  );
};

export default AnimeListScreen;
